    public static void Main()
    {
        var n = int.Parse(Console.ReadLine().Trim());
        var mo = new List<int>[32];
        for (int i = 1; i <= 31; i++) mo[i] = new List<int>();
        var ps = new P[n];
        for (int i = 0; i < n; i++)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            var a = int.Parse(line[0]);
            var b = int.Parse(line[1]);
            for (int j = a; j <= b; j++) mo[j].Add(i);
            ps[i] = new P { from = a, to = b };
        }
        var ret = getAns(ps, mo);
        Console.WriteLine(100 * ret + 50 * (31 - ret));

    }
    public static int getAns(P[] ps, List<int>[] mo)
    {
        var count = 0;
        for (int i = 1; i <= 31; i++)
        {
            var wlist = new List<int>();
            foreach (var x in mo[i])
                if (!ps[x].watched) wlist.Add(x);
            if (wlist.Count() == 1) { count++; ps[wlist[0]].watched = true; }
            else if (wlist.Count() > 1)
            {
                var a = getMovie(wlist, ps);
                count++;
                ps[a].watched = true;
            }
        }
        return count;
    }
    public static int getMovie(List<int> a, P[] ps)
    {
        var tomin = ps[a[0]].to;
        var ret = a[0];
        for (int i = 1; i < a.Count(); i++)
            if (ps[a[i]].to < tomin) { tomin = ps[a[i]].to; ret = a[i]; }
        return ret;
    }
}

