            var d = new Dictionary<char, int>();
            var dp = 0;
            for (int i = 0; i < h; i++)
            {
                var s = Console.ReadLine().Trim();
                for (int j = 0; j < w; j++)
                {
                    var ss = s[j];
                    if (ss != '.')
                    {
                        if (!d.ContainsKey(ss)) d[ss] = dp++;
                        map[i, j] = d[ss];
                    }
                    else map[i, j] = 9;
                }
            }
            var ps = getPs(map, dp);
            getAns(map, dp, ps);
        }
    }
    static bool check (int[,] map, P[] ps, int t)
    {
        for (int i = ps[t].xmin; i <= ps[t].xmax; i++)
            for (int j = ps[t].ymin; j <= ps[t].ymax; j++)
            {
                if (!(map[i, j] == t | map[i, j] == 8)) return false;
            }
        for (int i = ps[t].xmin; i <= ps[t].xmax; i++)
            for (int j = ps[t].ymin; j <= ps[t].ymax; j++)
                map[i, j] = 8;
        return true;

    }
    static void getAns (int[,] map, int dp, P[] ps )
    {
        var count = 0;
        while (true)
        {
            var change= false;
            for (int i = 0; i < h; i++)
            {
                for (int j = 0; j < w; j++)
                {
                    var t = map[i, j];
                    if (t <= 6)
                    {
                        if (check(map, ps, t)) { change = true; count++; }
                    }
                }
            }
            if (!change) break;
        }
        Console.WriteLine(count == dp? "SAFE": "SUSPICIOUS");
    }
    static P[] getPs(int[,] map, int dp)
    {
        var ps = new P[dp];
        for (int i = 0; i < dp; i++)
            ps[i] = new P { xmax = 0, ymax = 0, xmin = int.MaxValue, ymin = int.MaxValue };
        for (int i = 0; i < h; i++)
        {
            for (int j = 0; j < w; j++)
            {
                var mp = map[i, j];
                if (mp <= 6)
                {
                    ps[mp].xmax = Max(ps[mp].xmax, i);
                    ps[mp].ymax = Max(ps[mp].ymax, j);
                    ps[mp].xmin = Min(ps[mp].xmin, i);
                    ps[mp].ymin = Min(ps[mp].ymin, j);
                }
            }
        }
        return ps;
    }
