                line = Console.ReadLine().Trim().Split(' ');
                var t = int.Parse(line[0]);
                var s = int.Parse(line[1]);
                var d = int.Parse(line[2]);
                ps[i] = new P { t = t, s = s, d = d };
            }
            foreach (var x in ps.OrderBy(y => y.t))
                if (uf.IsSameGroup(x.s, 1)) uf.Unite(x.d, 1);
            var count = 1;
            for (int i = 2; i <= n; i++)
                if (uf.IsSameGroup(i, 1)) count++;
            Console.WriteLine(count);
        }
    }
}

