    if((n + 1) / 2 < k)
      cout << -1 << endl;
    else
      cout << solve() << endl;
    --c;
  }

  return 0;
}

ll solve() {
  ll l = 1, r = (n + 1) / 2, now, nownum, cnt, i, bit = 0,
     bf, store, allstore = (n + 1) / 2;
  //
  while(allstore > 0) {
    r = allstore;
    allstore = (allstore + 1) / 3;
    l = allstore + 1;
    //
    while(r - l >= 0) {
      //
      nownum = (l + r) / 2;
      now = (1 << (bit + 1)) * nownum - (1 << bit);
      cnt = 0;
      store = (n + 1) / 2;
      // 2^i
      for(i = 0; (1 << i) <= n && store != 0; ++i) {
        bf = (1 << (i + 1)) * store - (1 << i);
        store = (store + 1) / 3;
        ll p = ((1 << i) + min(bf, now)) / (1 << (i + 1)) -
               store;
        cnt += max((ll)0, p);
      }
      if(cnt == k)
        return now;
      else if(r - l == 1)
        l = r;
      else if(r - l == 0)
        break;
      else if(cnt > k)
        r = nownum;
      else
        l = nownum;
    }
    ++bit;
  }
  return -1;
}

