  def initialize(l, r)
    @l, @r = l, r
    @lazy = nil
    if l == r
      @completed = 0
    else
      c = (l + r) / 2
      @left_child = self.class.new(l, c)
      @right_child = self.class.new(c + 1, r)
    end
  end

  attr_reader :l, :r

  def update(l, r, x)
    if l == @l && r == @r
      defer(x)
    else
      force
