    return if !@lazy
    x = @lazy
    @lazy = nil
    whole_update(x) if @l == @r
    @left_child.update(@left_child.l, @left_child.r, x) if @left_child
    @right_child.update(@right_child.l, @right_child.r, x) if @right_child
  end

  def query(i)
    force
    return @completed if @l == @r
    if i <= @left_child.r
      return @left_child.query(i)
    else @right_child.l <= i
      return @right_child.query(i)
    end
  end
end

