/* Starry Sky Tree */
//0-index

struct StarrySkyTree{
  typedef ll Type;
  int segn2;
  vector<pair<ll,int>> data;
  vector<Type> s_data;
  function<Type(Type, Type)> merge;

  StarrySkyTree(function<Type(Type, Type)> merge, int n): merge(merge)
  {
    for(segn2=1; segn2<n; segn2*=2);
    data.assign(segn2*2, {0, 0});
    s_data.assign(segn2*2, 0);
  }

  StarrySkyTree(int n):
    StarrySkyTree([](Type a, Type b){ return a + b; }, n) {}

  //get value of [a,b)
  pair<ll,int> query(int a, int b, int l = 0, int r = -1, int k = 0){
    if(r == -1) r = segn2;
    if(r <= a || b <= l) return {0, 0}; //
    if(a <= l && r <= b) return {data[k].first + data[k].second * s_data[k], data[k].second};

    auto p1 = query(a, b, l, (l+r)/2, k*2+1);
    auto p2 = query(a, b, (l+r)/2, r, k*2+2);

    int num = p1.second + p2.second;
    ll sum = p1.first + p2.first + num * s_data[k];

    return {sum, num};
  }

  //add x to [a,b)
  pair<ll,int> add(int a, int b, Type x, int l = 0, int r = -1, int k = 0){
    if(r == -1) r = segn2;
    if(a <= l && r <= b) {
      s_data[k] += x;
    } else if(a < r && l < b) {
      auto p1 = add(a, b, x, l, (l+r)/2, k*2+1);
      auto p2 = add(a, b, x, (l+r)/2, r, k*2+2);
      int num = p1.second + p2.second;
      ll sum = p1.first + p2.first;
      data[k] = {sum, num};
    }

    return {data[k].first + s_data[k] * data[k].second, data[k].second};
  }

  //add x to [a,b)
  pair<ll,int> add2(int a, int b, Type x, int l = 0, int r = -1, int k = 0){
    if(r == -1) r = segn2;
    if(a <= l && r <= b)
      data[k].second += x;
    else if(a < r && l < b) {
      auto p1 = add2(a, b, x, l, (l+r)/2, k*2+1);
      auto p2 = add2(a, b, x, (l+r)/2, r, k*2+2);
      int num = p1.second + p2.second;
      ll sum = p1.first + p2.first;
      data[k] = {sum, num};
    }

    return {data[k].first + s_data[k] * data[k].second, data[k].second};
  }

};

