	public int x { get; set; }
	public int y { get; set; }
	public int step { get; set; }
}

public class hello
{
	public static void Main()
	{
		var map = new int[3, 3];
		var sx = 0;  var sy = 0;
		for (int i = 0; i < 3; i++)
		{
			string[] line = Console.ReadLine().Trim().Split(' ');
			for (int j = 0; j < 3; j++)
			{
				map[i, j] = int.Parse(line[j]);
				if (map[i,j] == 0) { sx = i; sy = j; };
			}
		}
		var ans = goBfs(map, sx, sy);
		Console.WriteLine(ans);
	}
	static int goBfs (int [,] map, int sx , int sy)
	{
		var hs = new HashSet<string>();
		var dx = new int[] { 0, 1, 0, -1 };
		var dy = new int[] { 1, 0, -1, 0 };
		var q = new Queue<P>();
		var t = MapToString(map);
		q.Enqueue(new P { s = t, x = sx,y = sy,step = 0});
		hs.Add(t);
		while (q.Count() > 0)
		{
			var w = q.Dequeue();
			if (w.s == "123456780") return w.step;
			var tmap = StringToMap(w.s);
			for (int i = 0; i < 4; i++)
			{
				var nx = w.x + dx[i];
				var ny = w.y + dy[i];
				if (nx >= 0 && nx < 3 && ny >= 0 && ny < 3)
				{
					tmap[w.x, w.y] = tmap[nx, ny];
					tmap[nx, ny] = 0;
					var tt= MapToString(tmap);
					if (hs.Add(tt)) q.Enqueue(new P { s = tt, x = nx, y = ny, step = w.step + 1 });
					tmap[nx, ny] = tmap[w.x, w.y];
					tmap[w.x, w.y] = 0;
				}
			}
		}
		return 0;
	}
	static string MapToString (int [,] map)
	{
		var s = "";
		foreach (var x in map) s += x;
		return s;
	}
	static int[,] StringToMap (string s)
	{
		var map = new int[3, 3];
