template<class T>
struct SegTreeT2
{
	int size;
	vector<T> tree;
	vector<pair<bool, T>> lazy;

	T initialValue;
	static constexpr unsigned int msb(unsigned int v) { REP(i, 5) v |= v >> (1 << i); return (v >> 1) + 1; }

	SegTreeT2(int count, T initialValue)
		: size(msb(count - 1) << 1)
		, tree(size * 2)
		, lazy(size * 2)
		, initialValue(initialValue)
	{
		lazy[1] = { true, initialValue };
	}

	void push(int l, int r, int i)
	{
		if (lazy[i].first)
		{
			tree[i] += lazy[i].second;
			if (r - l > 1)
			{
				lazy[i * 2] = { true, lazy[i * 2].second + lazy[i].second };
				lazy[i * 2 + 1] = { true, lazy[i * 2 + 1].second + lazy[i].second };
			}
			lazy[i] = { false, 0 };
		}
	}

	void update_range(int begin, int end, T value)
	{
		update_range(begin, end, 1, 0, size, value);
	}

	void update_range(int s, int e, int i, int l, int r, T value)
	{
		push(l, r, i);

		if (r <= s || e <= l) { return; }
		if (s <= l && r <= e) { lazy[i] = { true, lazy[i].second + value }; return; }

		int m = (l + r) / 2;
		if (s < m) { update_range(s, e, i * 2, l, m, value); }
		if (e > m) { update_range(s, e, i * 2 + 1, m, r, value); }
	}

	T query(int s, int e, int i, int l, int r, T value)
	{
		push(l, r, i);
		if (r <= s || e <= l) { return value; }
		if (s <= l && r <= e) { return tree[i]; }

		int m = (l + r) / 2;
		if (s < m) { value += query(s, e, i * 2, l, m, value); }
		if (e > m) { value += query(s, e, i * 2 + 1, m, r, value); }
		return value;
	}

	T query(int begin, int end, T initialValue) { return query(begin, end, 1, 0, size, initialValue); }
	T query(int begin, int end) { return query(begin, end, initialValue); }
};


