        var pq = new PriorityQueue<P>(n + L, 0);
        for (int i = 0; i < L; i++)
            pq.Push(new P { id = i, e = a[i] });
        var p = 0;
        var nxa = L;
        while (p < n - L + 1)
        {
            while (true)
            {
                if (pq.Peek().id < p) pq.Pop();
                else break;
            }
            ans[ansp++] = pq.Peek().e;
            if (nxa < n)
