void swap(node *a,node *b){
  node t=*a;
  *a=*b;
  *b=t;
  return;
}

void push(node *h,int v,int val){
  h[0].v++;
  int k=h[0].v;
  h[k].v=v;
  h[k].val=val;
  while(k>1){
    if(cmp(h+k/2,h+k)) return;
    swap(h+k/2,h+k);
    k/=2;
  }
  return;
}

int pop(node *h){
  int res=h[1].v;
  h[1]=h[h[0].v--];
  int k=1;
  int n=h[0].v;
  while(2*k+1<=n){
    if(cmp(h+k,h+2*k) && cmp(h+k,h+2*k+1)) break;
    int next=cmp(h+2*k,h+2*k+1)?2*k:2*k+1;
    swap(h+k,h+next);
    k=next;
  }
  if(2*k<=n && !cmp(h+k,h+2*k)) swap(h+k,h+2*k);
  return res;
}

