template <typename X, typename M>
struct segment_tree_lazy {
    using FX = function<X(X, X)>;
    using FA = function<X(X, M)>;
    using FM = function<M(M, M)>;
    using FP = function<M(M, int)>;
    int n;
    FX fx;
    FA fa;
    FM fm;
    const X ex;
    const M em;
    vector<X> dat;
    vector<M> lazy;
    segment_tree_lazy(FX fx_, FA fa_, FM fm_, X ex_, M em_)
        : fx(fx_), fa(fa_), fm(fm_), ex(ex_), em(em_) {
    }

    void set(int i, X x) { dat[i + n - 1] = x; }
    void init(int n_) { 
        n = 1;
        while (n < n_) n <<= 1;
        dat.assign(2 * n, ex);
        lazy.assign(2 * n, em);
    } 

    void build(const vector<X> &v) { 
        int n_ = v.size();
        init(n_);
        for (int i = 0; i < n_; i++) dat[i + n - 1] = v[i];
        for (int k = n - 2; k >= 0; k--) dat[k] = fx(dat[2 * k + 1], dat[2 * k + 2]);
    } 

    /* lazy eval */
    void eval(int k) {
        if (lazy[k] == em) return;  // 
        if (k < n - 1) {            // 
            lazy[k * 2 + 1] = fm(lazy[k * 2 + 1], lazy[k]);
            lazy[k * 2 + 2] = fm(lazy[k * 2 + 2], lazy[k]);
        }
        // 
        dat[k] = fa(dat[k], lazy[k]);
        lazy[k] = em;
    }

    void update(int a, int b, M x, int k, int l, int r) {
        eval(k);
        if (a <= l && r <= b) {  // 
            lazy[k] = fm(lazy[k], x);
            eval(k);
        } else if (a < r && l < b) {                     // 
            update(a, b, x, k * 2 + 1, l, (l + r) / 2);  // 
            update(a, b, x, k * 2 + 2, (l + r) / 2, r);  // 
            dat[k] = fx(dat[k * 2 + 1], dat[k * 2 + 2]);
        }
    }
    void update(int a, int b, M x) { update(a, b, x, 0, 0, n); }

    X query_sub(int a, int b, int k, int l, int r) {
        eval(k);
        if (r <= a || b <= l) {  // 
            return ex;
        } else if (a <= l && r <= b) {  // 
            return dat[k];
        } else {  // 
            X vl = query_sub(a, b, k * 2 + 1, l, (l + r) / 2);
            X vr = query_sub(a, b, k * 2 + 2, (l + r) / 2, r);
            return fx(vl, vr);
        }
    }
    X query(int a, int b) { return query_sub(a, b, 0, 0, n); }

};

/* SegTreeLazyProportional<X,M>(n,fx,fa,fm,fp,ex,em): (X, fx,fa,fm,p ex,em)n
    set(int i, X x), build(): ixO(n)
    update(i,x): i  x O(log(n))
    query(a,b):  [a,b) fxO(log(n))
*/

