                val to = edges[pos][i]
                if (to == p) continue
                dfs1(to, pos)
                s += size[to]
                memo[pos] *= memo[to]
                memo[pos] *= fact[size[to]].inverse()
            }
            memo[pos] *= fact[s]
            size[pos] = s + 1
        }

        dfs1(0, -1)
        // println(memo.joinToString(" "))
