            for (i in edges[pos].indices) {
                val to = edges[pos][i]
                if (to == p) continue
                val nextPP = ans[pos] * (fact[n - 1] * memo[to]).inverse() * fact[size[to]] * fact[n - size[to] - 1]
                dfs2(to, pos, nextPP)
            }
        }
