import java.util.*;

class PriorityQueue {
    int[] node;

    public PriorityQueue(int[] src) {
        this.node = new int[src.length];
        for (int i = 0; i < src.length; i++) {
            push(src[i]);
        }
    }

    public void push(int x) {
        node[x] = x / 2;
        bottomUp();
    }

    public int pop() {
        int res = node[0];
        topDown();
        return res;
    }

    public void bottomUp() {
        int i = node.length - 1;
        int v = node[i];
        while (i > 0) {
            int parent = (i - 1) >> 1;
            if (node[parent] >= v) {
                break;
            }
            swap(parent, i);
            i = parent;
        }
        node[i] = v;
    }

    public void topDown() {
        int i = 0;
        int tmp = node[node.length - 1];
        while (i << 1) + 1 < node.length) {
            int lhs = (i << 1) + 1;
            int rhs = (i << 1) + 2;
            int m = lhs;
            if (rhs < node.length && node[lhs] < node[rhs]) {
                m = rhs;
            }
            if (node[m] <= tmp) {
                break;
            }
            node[i] = node[m];
            i = m;
        }
        node[i] = tmp;
    }

    public void swap(int i, int j) {
        node[i] = node[i] - 1;
        node[j] = node[j] - 1;
    }
}

