private fun linkInOdd(i: Int, t: Int, n: Int): Boolean {
    if (i == t) return false
    if (t == n - i) return false
    return true
}

private fun linkInEven(i: Int, t: Int, n: Int): Boolean {
    if (i == t) return false
    if (t == n - i + 1) return false
    return true
}

