    private int[] dt;

    public UnionFind(int N) {
        this.dt = new int[N + 1];
        Arrays.fill(this.dt, -1);
    }

    public int root(int key) {
        if (this.dt[key] < 0) {
            return key;
        } else {
            this.dt[key] = this.root(this.dt[key]);
            return this.dt[key];
        }
    }

    public boolean unite(int KX, int KY) {
        int kx = root(KX);
        int ky = root(KY);
        if (kx == ky) {
