import java.util.*;

class UnionFind {
    private int[] p;
    private int[] components;

    public UnionFind(int n) {
        this.p = new int[n];
        this.components = new int[n];
        for (int i = 0; i < n; i++) {
            this.p[i] = i;
            this.components[i] = 1;
        }
    }

    public int root(int x) {
        if (this.p[x]!= x) {
            Stack<Integer> stack = new Stack<>();
            while (true) {
                int top = stack.peek() - 1;
                if (this.p[top] == top) {
                    break;
                } else {
                    stack.push(this.p[top]);
                }
            }
            for (int v : stack) {
                this.p[v] = top;
            }
        }
        return this.p[x];
    }

    public void unite(int x, int y) {
        x = root(x);
        y = root(y);
        if (x!= y) {
            this.p[x] = y;
            int sizeX = components[x];
            int sizeY = components[y];
            components[x] = sizeX + sizeY;
            components[y] = sizeX + sizeY;
        }
        return sizeX * sizeY;
    }

    public boolean same(int x, int y) {
        return root(x) == root(y);
    }
}

