#include<stdio.h>
#define mod 1000000007

long long power(long long a,long long b){
  long long x=1,y=a;
  while(b>0){
    if(b%2){
      x=(x*y)%mod;
    }
    y=(y*y)%mod;
    b/=2;
  }
  return x%mod;
}

long long modular_inverse(long long n){
  return power(n,mod-2);
}

long long inv[1024];

void invcalc(){
  long long i;
  for(i=0;i<1024;i++){
    inv[i]=modular_inverse(i);
  }
}

void indefinite_integral(long long f[1024]){
  long long i;
  for(i=1022;i>=0;i--){
    f[i+1]=inv[i+1]*f[i];
    f[i+1]%=mod;
  }
  f[0]=0;
}

long long fcalc(long long f[1024],long long x){
  long long i,r=0;
  if(x==0){
    return f[0];
  }
  if(x==1){
    for(i=0;i<1024;i++){
      r+=f[i];
      r%=mod;
    }
    return r;
  }
}

typedef struct{
  long long p[1024];
  long long q[1024];
  long long c;
}func;

void integral_ztx(func *a,func *b){
  long long w,i;

  //func_add
  for(i=0;i<1024;i++){
    (((func*)a)->p[i])+=(((func*)b)->p[i]);
    (((func*)a)->p[i])%=mod;
    (((func*)a)->q[i])+=(((func*)b)->q[i]);
    (((func*)a)->q[i])%=mod;
  }
  (((func*)a)->c)+=(((func*)b)->c);
  (((func*)a)->c)%=mod;

  w=fcalc(((func*)b)->p,0);
  (((func*)a)->p[0])+=mod;
  (((func*)a)->p[0])-=w;
  (((func*)a)->p[0])%=mod;

  w=fcalc(((func*)b)->q,0);
  (((func*)a)->q[0])+=mod;
  (((func*)a)->q[0])-=w;
  (((func*)a)->q[0])%=mod;

  w=((func*)b)->c;
  (((func*)a)->p[0])+=mod;
  (((func*)a)->p[0])-=w;
  (((func*)a)->p[0])%=mod;
}

void integral_xto(func *a,func *b){
  long long w,i;

  //func_dec
  for(i=0;i<1024;i++){
    (((func*)a)->p[i])+=mod;
    (((func*)a)->p[i])-=(((func*)b)->p[i]);
    (((func*)a)->p[i])%=mod;
    (((func*)a)->q[i])+=mod;
    (((func*)a)->q[i])-=(((func*)b)->q[i]);
    (((func*)a)->q[i])%=mod;
  }
  (((func*)a)->c)+=mod;
  (((func*)a)->c)-=(((func*)b)->c);
  (((func*)a)->c)%=mod;

  w=fcalc(((func*)b)->p,1);
  (((func*)a)->p[0])+=w;
  (((func*)a)->p[0])%=mod;

  w=fcalc(((func*)b)->q,1);
  (((func*)a)->q[0])+=w;
  (((func*)a)->q[0])%=mod;

  w=((func*)b)->c;
  (((func*)a)->q[0])+=w;
  (((func*)a)->q[0])%=mod;
}

