    dp[0][0][0][1].c=1;
    dp[0][1][1][1].p[0]=1;
    dp[0][1][1][1].c=mod-1;
  }

  for(i=1;i<n;i++){
    for(j=0;j<2;j++){
      for(k=0;k<2;k++){
        for(l=0;l<2;l++){
          indefinite_integral(dp[i-1][j][k][l].p);
          indefinite_integral(dp[i-1][j][k][l].q);
          dp[i-1][j][k][l].c=mod-dp[i-1][j][k][l].c;
          dp[i-1][j][k][l].c%=mod;
        }
