class Program
{

    static void Main(string[] args)
    {

        //var NX = Console.ReadLine().Split(' ').Select(x => long.Parse(x)).ToArray();
        var NK = Console.ReadLine().Split(' ').Select(x => long.Parse(x)).ToArray();
        var N = NK[0];
        var K = NK[1];
        var A = Console.ReadLine().Split(' ').Select(x => long.Parse(x)).ToArray();

        var cumA = new long[N + 1];
        for (int i = 1; i < N + 1; i++)
        {
            cumA[i] = cumA[i - 1] + A[i - 1];
        }
        var Sums = new long[N*(N+1)/2];

        var count = 0;
        for (int l = 1; l <= N; l++)
        {
            for (int r = l; r <= N; r++)
            {
                Sums[count] = cumA[r] - cumA[l - 1];
                count++;
            }
        }
        Array.Sort(Sums);
        Array.Reverse(Sums);

        long ans = 0;
        var MaxNumBin = Convert.ToString(Sums[0], 2);
        for (int i = MaxNumBin.Length - 1; i >= 0; i--)
        {
            long now = ans + (long)Math.Pow(2, i);
            long OK = 0;
            foreach (var item in Sums)
            {
                if((item & now) > ans)
                {
                    OK++;
                }
            }

            if (OK >= K) ans = now;
        }
        Console.WriteLine(ans);
    }
}

