        for (int i = MaxNumBin.length() - 1; i >= 0; i--) {
            long now = ans + (long) Math.pow(2, i);
            long OK = 0;
            for (int item : sums) {
                if ((item & now) > ans) {
                    OK++;
                }
            }

            if (OK >= K) ans = now;
        }
        System.out.println(ans);
    }
}

