class Solve{
    public Solve(){}
    StringBuilder sb;
    ReadData re;
    public static int Main(){
        new Solve().Run();
        return 0;
    }
    void Run(){
        sb = new StringBuilder();
        re = new ReadData();
        Calc();
        Console.Write(sb.ToString());
    }
    void Calc(){
        long[] X = new long[4];
        long[] reX = new long[4];
        X[0] = 118306800;
        X[1] = 686550364;
        X[2] = 218792700;
        X[3] = 202607072;
        for(int i=0;i<4;i++){
            reX[i] = Pow(X[i],Define.mod-2);
        }
        int N = re.i();
        string S = re.s();
        //Si H(0) = Sj(0)
        long[] Place = new long[4];
        long[,] SJ = new long[N+1,4];
        for(int i=0;i<4;i++){
            Place[i] = 1;
        }
        for(int i=0;i<N;i++){
            for(int j=0;j<4;j++){
                if(S[i] == '+'){
                    SJ[i+1,j] = (SJ[i,j] + Place[j]) % Define.mod; 
                }
                if(S[i] == '-'){
                    SJ[i+1,j] = (SJ[i,j] - Place[j] + Define.mod) % Define.mod;
                }
                if(S[i] == '<'){
                    SJ[i+1,j] = SJ[i,j];
                    Place[j] = Place[j] * reX[j] % Define.mod;
                }
                if(S[i] == '>'){
                    SJ[i+1,j] = SJ[i,j];
                    Place[j] = Place[j] * X[j] % Define.mod;
                }
            }
        }
        long[,] SiH = new long[N+1,4];
        for(int i=0;i<4;i++){
            Place[i] = 1;
        }
        for(int i=0;i<=N;i++){
            for(int j=0;j<4;j++){
                if(i != 0){
                    if(S[i-1] == '<'){
                        Place[j] = Place[j] * reX[j] % Define.mod;
                    }
                    if(S[i-1] == '>'){
                        Place[j] = Place[j] * X[j] % Define.mod;
                    }
                }
                SiH[i,j] = (SJ[i,j] + Place[j] * SJ[N,j]) % Define.mod;
            }
        }
        int[] C = new int[N+1];
        for(int i=0;i<=N;i++){
            C[i] = i;
        }
        Array.Sort(C,(x,y)=>Compare(x,y,SJ));
        long count = 0;
        for(int i=0;i<N;i++){
            int bf = 0;
            int bl = N+1;
            while(bf != bl){
                int bc = (bf+bl)/2;
                if(bc == N+1 || OK1(i,C[bc],SiH,SJ)){
                    bl = bc;
                }
                else{
                    bf = bc+1;
                }
            }
            int min = bf;
            bf = -1;
            bl = N;
            while(bf != bl){
                int bc = (bf+bl+1)/2;
                if(bc == -1 || OK2(i,C[bc],SiH,SJ)){
                    bf = bc;
                }
                else{
                    bl = bc-1;
                }
            }
            int max = bf;
            count += max - min + 1;
        }
        sb.Append(count+"\n");
    }
    bool OK1(int i,int v,long[,] SiH,long[,] SJ){
        for(int j=0;j<4;j++){
            if(SiH[i,j] > SJ[v,j]){
                return false;
            }
            if(SiH[i,j] < SJ[v,j]){
                return true;
            }
        }
        return i < v;
    }
    bool OK2(int i,int v,long[,] SiH,long[,] SJ){
        for(int j=0;j<4;j++){
            if(SiH[i,j] > SJ[v,j]){
                return true;
            }
            if(SiH[i,j] < SJ[v,j]){
                return false;
            }
        }
        return true;
    }
    int Compare(int x,int y,long[,] S){
        for(int i=0;i<4;i++){
            if(S[x,i] > S[y,i]){
                return 1;
            }
            if(S[x,i] < S[y,i]){
                return -1;
            }
        }
        return x - y;
    }
    long Pow(long x,long r){
        if(r == 0){
            return 1;
        }
        if(r % 2 == 0){
            long t = Pow(x,r/2);
            return t*t%Define.mod;
        }
        return Pow(x,r-1)*x%Define.mod;
    }
}
