        while (i > 0) {
            res += bit[i];
            i -= i & -i;
        }
        return res;
    }

    public static int bitAdd(int[] bit, int i, int val) {
        int n = bit.length;
        for (int i = 0; i < n; i++) {
            bit[i] += val;
            i += i & -i;
        }
    }
}

