  scanf("%lld%lld",a,a+1);
  if(!(a[0]%3*a[1]%3)){printf("0");return 0;}
  rep(i,2){//
    LL x=a[i],y=a[i?0:1],z[3],s=x*y;
    Rep(j,Ma(1,x/3-2),mi(x,x/3+3)){//||,|-
      z[0]=y*j,z[1]=y*((x-j)/2),z[2]=s-z[0]-z[1];
      qsort(z,3,8,upLL);
      ans=mi(ans,z[2]-z[0]);
      z[0]=y*j,z[1]=(y/2)*(x-j),z[2]=s-z[0]-z[1];
      qsort(z,3,8,upLL);
      ans=mi(ans,z[2]-z[0]);
