using System.Linq;//
using System.Collections.Generic;
using System.Text;//
class Program
{
	static void Main()
	{
		string[] input = Console.ReadLine().Split(' ');
		long n = long.Parse(input[0]);
		long d = long.Parse(input[1]);
		long[] numsd = Array.ConvertAll(Console.ReadLine().Split(' '),long.Parse);
    long q = long.Parse(Console.ReadLine());
    long[] numsq = Array.ConvertAll(Console.ReadLine().Split(' '),long.Parse);
    bool[] answers = new bool[n];
    long[] rests = new long[n+1];
    rests[0] = d;
    long[] noMin = new long[n+1];
    noMin[n] = 1;

    for(long i = 0; i < n; i++)
    {
      rests[i+1] = rests[i];
      if(Math.Abs(rests[i]-numsd[i]) < rests[i]) rests[i+1] = Math.Abs(rests[i]-numsd[i]);

      //Console.WriteLine(i+" "+rests[i]+"r");
    }

    for(long i = 0; i < n; i++)
    {
      noMin[n-1-i] = noMin[n-i];
      if((noMin[n-1-i]-1)*2+1 >= numsd[n-1-i]) noMin[n-1-i] += numsd[n-1-i];
      //Console.WriteLine((n-1-i)+" "+noMin[n-1-i]);
    }

    for(long i = 0; i < n; i++)
    {
      if(noMin[i+1] <= rests[i]) answers[i] = true;
    }

    for(long i = 0; i < q; i++)
    {
      if(answers[numsq[i]-1]) Console.WriteLine("YES");
      else Console.WriteLine("NO");
    }
	}
}

