#include <stdio.h>

typedef struct {
	int key, id;
} data;

typedef struct {
	data obj[300001];
	int size;
} min_heap;

void push(data x, min_heap* h)
{
	int i = ++(h->size);
	data tmp;
	h->obj[i] = x;
	while (i > 1) {
		if (h->obj[i].key < h->obj[i>>1].key) {
			tmp = h->obj[i>>1];
			h->obj[i>>1] = h->obj[i];
			h->obj[i] = tmp;
			i >>= 1;
		} else break;
	}
}

data pop(min_heap* h)
{
	int i = 1, j = 2;
	data output = h->obj[1], tmp;
	h->obj[1] = h->obj[(h->size)--];
	while (j <= h->size) {
		if (j < h->size && h->obj[j^1].key < h->obj[j].key) j ^= 1;
		if (h->obj[j].key < h->obj[i].key) {
			tmp = h->obj[j];
			h->obj[j] = h->obj[i];
			h->obj[i] = tmp;
			i = j;
			j = i << 1;
		} else break;
	}
	return output;
}

void add_BIT(int N, int BIT[], int i, int k)
{
	while (i <= N) {
		BIT[i] += k;
		i += (i & -i);
	}
}

int sum_BIT(int BIT[], int r)
{
	int sum = 0;
	while (r > 0) {
		sum += BIT[r];
		r -= (r & -r);
	}
	return sum;
}

int main()
{
	int i, N, M, l[300001], r[300001];
	data d;
	min_heap h;
	h.size = 0;
	scanf("%d %d", &N, &M);
	for (i = 1; i <= N; i++) {
		scanf("%d %d", &(l[i]), &(r[i]));
		d.key = r[i] - l[i] + 1;
		d.id = i;
		push(d, &h);
	}

	int j, BIT[2][100001] = {}, ans[100001] = {};
	for (i = 2, ans[1] = N; i <= M; i++) {
		while (h.size > 0 && h.obj[1].key <= i) {
			d = pop(&h);
			add_BIT(M, BIT[0], r[d.id], 1);
			add_BIT(M, BIT[1], M - l[d.id] + 1, 1);
		}
		for (j = i, ans[i] = h.size; j <= M; j += i) ans[i] += N - h.size - sum_BIT(BIT[0], j - 1) - sum_BIT(BIT[1], M - j);
	}
	for (i = 1; i <= M; i++) printf("%d\n", ans[i]);
	fflush(stdout);
	return 0;
}

