public class Main {
    static long ksm(long a, long b) {
        long ans = 1, base = a;
        while (b > 0) {
            if ((b & 1)!= 0) ans = ans * base % 1000000007;
            b >>= 1;
            base = base * base % 1000000007;
        }
        return ans;
    }

    static long getans(long x) {
        return ksm(k, (x - 1) / 2 + 1);
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long ans = 0;
        long[] fact = new long[n + 1];
        long[] dp = new long[n + 1];
        for (int i = 1; i * i <= n; i++) {
            if (n % i == 0) {
                fact[i] = i;
                if (i!= n / i)
                    fact[i] = n / i;
            }
        }
        Arrays.sort(fact);
        long m = fact.length;
