	}
	for (head--; head > 0; head--) {
		i = q[head];
		if (height[i] == K - 1 && a[i] != 1) {
			height[i] = -1;
			a[i] = 1;
			ans++;
		}
		if (height[a[i]] < height[i] + 1) height[a[i]] = height[i] + 1;
	}	
